import { checkEndGame } from '../../tools/checkEndGame'
import printBoard from '../../tools/printBoard'
import { getStonePosition, initBoard, serverIp } from '../../tools/tools'
import { io } from "socket.io-client"
import styles from './humanVsAi.module.css'

const canvasOnMouseMove = (e, data) => {
    if (data.endGame || data.turn === 'ai' || data.ongoingAnimation)
        return
    var canvasOffsetWidth = data.canvas.offsetWidth
    var canvasOffsetHeight = data.canvas.offsetHeight
    var stone = getStonePosition(data, e, canvasOffsetWidth, canvasOffsetHeight)
    if (stone.y === 19)
        return
    if (!data.board[stone.y][stone.x]){
        printBoard(data)
        data.ctx.globalAlpha = 0.5
        data.ctx.drawImage(
            data.playerColor === data.WHITE ? data.images.whitePiece : data.images.blackPiece,
            data.images.gomokuBoard.width * stone.x,
            data.images.gomokuBoard.height * stone.y
        )
        data.ctx.globalAlpha = 1
    }
    else
        printBoard(data)
}

const printEndGameStatus = data => {
	var params = {}
    params[data.DRAW] = {
        text : 'END OF GAME, NO WINNER',
        color : 'orange'
    }
    params[data.playerColor] = {
        text : 'YOU WON THE GAME !',
        color : 'green'
    }
    params[data.aiColor] = {
        text : 'YOU LOST THE GAME',
        color : 'red'
    }
    document.getElementById('endGame').style.color = params[data.endGame].color
    document.getElementById('endGame').innerHTML = params[data.endGame].text
    document.getElementById('newGame').style.display = 'block'
}

const setEndGame = data => {
    printEndGameStatus(data)
	data.socket.disconnect()
}

const launchAi = data => {
	data.startAi = window.performance.now()
	data.aiLaunched = true
	data.socket.emit('ai', {aiColor : data.aiColor, board : data.board})
}

const printNewStone = (data, newStone, pos, color) => {
	var stone
	if (color === data.BLACK)
		stone = data.images.blackPiece
	else
		stone = data.images.whitePiece
	data.ctx.drawImage(
		stone,
		data.images.gomokuBoard.width * pos.x - ((newStone.width - data.images.gomokuBoard.width) / 2),
		data.images.gomokuBoard.height * pos.y - ((newStone.height - data.images.gomokuBoard.height) / 2),
		newStone.width,
		newStone.height
	)
}

const check_capture = (data, captured, pos, xIncrement, yIncrement, color, ennemyColor) => {
	var x;
	var y;

	x = pos.x + xIncrement
	y = pos.y + yIncrement
	if (!(x >= 0 && x < 19 && y >= 0 && y < 19
	&& data.board[y][x] === ennemyColor))
		return
	x += xIncrement
	y += yIncrement
	if (!(x >= 0 && x < 19 && y >= 0 && y < 19
	&& data.board[y][x] === ennemyColor))
		return ;
	x += xIncrement
	y += yIncrement
	if (!(x >= 0 && x < 19 && y >= 0 && y < 19
		&& data.board[y][x] === color))
		return ;
	captured.push({x : pos.x + xIncrement, y : pos.y + yIncrement})
	captured.push({x : pos.x + 2 * xIncrement, y : pos.y + 2 * yIncrement})
}

const getCapturedStones = (data, captured, pos, color, ennemyColor) => {

	check_capture(data, captured, pos, 0, -1, color, ennemyColor)
	check_capture(data, captured, pos, 1, -1, color, ennemyColor)
	check_capture(data, captured, pos, 1, 0, color, ennemyColor)
	check_capture(data, captured, pos, 1, 1, color, ennemyColor)
	check_capture(data, captured, pos, 0, 1, color, ennemyColor)
	check_capture(data, captured, pos, -1, 1, color, ennemyColor)
	check_capture(data, captured, pos, -1, 0, color, ennemyColor)
	check_capture(data, captured, pos, -1, -1, color, ennemyColor)
}

const printCapturedStones = (data, captured, sizeStones, color) => {
	var stone

	if (color === data.BLACK)
		stone = data.images.blackPiece
	else
		stone = data.images.whitePiece
	for (var c = 0; c < captured.length; c += 1){
		data.ctx.drawImage(
			stone,
			data.images.gomokuBoard.width * captured[c].x - ((sizeStones.width - data.images.gomokuBoard.width) / 2),
			data.images.gomokuBoard.height * captured[c].y - ((sizeStones.height - data.images.gomokuBoard.height) / 2),
			sizeStones.width,
			sizeStones.height
		)
	}
}

const getTwoCapturedStones = color => {
	var div

	div = document.createElement('div')
	div.className = styles.
	image = document.createElement('img')
	image.className = styles.capturedStoneImg
	if (color === data.BLACK)
		image.src = data.images.whitePiece.src
	else
		image.src = data.images.blackPiece.src
}

const updateCapturedStones = (data, color, captures) => {
	var image
	var html

	if (color === data.aiColor){
		html = document.getElementById('aiStonesCaptured')
		document.getElementById('aiCaptures').innerHTML = `${data.captures.ai} / 10`;
	}
	else{
		html = document.getElementById('playerStonesCaptured')
		document.getElementById('playerCaptures').innerHTML = `${data.captures.player} / 10`;
	}
	for (var c = 0; c < captures; c += 2){

		html.append(getTwoCapturedStones(color))
	}
}

const goToNextPlayer = (data, nextPlayer, color, captures) => {

	if (captures)
		updateCapturedStones(data, color, captures)
	if ((data.endGame = checkEndGame(data))){
		setEndGame(data)
		return
	}
	if (nextPlayer === 'human'){
		data.turn = 'human'
		data.aiLaunched = false
	}
	else if (nextPlayer === 'ai'){
		data.turn = 'ai'
		launchAi(data, data.images, data.playerColor)
	}
	data.ongoingAnimation = 0
}

const launchCaptureAnimation = (data, pos, color, nextPlayer) => {
	var captured = [];
	var ennemyColor;
	if (color === data.BLACK)
		ennemyColor = data.WHITE
	else
		ennemyColor = data.BLACK
	getCapturedStones(data, captured, pos, color, ennemyColor)
	if (!captured.length){
		goToNextPlayer(data, nextPlayer, color, 0)
		return
	}
	if (color === data.aiColor)
		data.captures.ai += captured.length
	else
		data.captures.player += captured.length
	for (var c = 0; c < captured.length; c += 1){
		data.board[captured[c].y][captured[c].x] = 0;
	}
	data.piecesOnBoard -= captured.length
	var sizeStones = {}
	sizeStones.width = data.images.gomokuBoard.width
	sizeStones.height = data.images.gomokuBoard.height
	var inter = setInterval(() => {
		printBoard(data)
		printCapturedStones(data, captured, sizeStones, ennemyColor)
		sizeStones.width -= data.images.gomokuBoard.width / 70
		sizeStones.height -= data.images.gomokuBoard.height / 70
		if (sizeStones.width <= 0 && sizeStones.height <= 0){
			clearInterval(inter)
			goToNextPlayer(data, nextPlayer, color, captured.length)
		}
	}, 10)
}

const launchPutStoneAnimation = (data, pos, color, nextPlayer) => {
	data.ongoingAnimation = 1
	var newStone = {}
	newStone.width = data.images.gomokuBoard.width * 10;
	newStone.height = data.images.gomokuBoard.height * 10;
	var inter = setInterval(() => {
		printBoard(data)
		printNewStone(data, newStone, pos, color);
		newStone.width -= data.images.gomokuBoard.width / 4;
		newStone.height -= data.images.gomokuBoard.height / 4;
		if (newStone.width <= data.images.gomokuBoard.width &&
		newStone.height <= data.images.gomokuBoard.height){
			data.board[pos.y][pos.x] = color
			data.piecesOnBoard += 1;
			printBoard(data);
			clearInterval(inter)
			launchCaptureAnimation(data, pos, color, nextPlayer);
		}
	}, 10);
}

const receiveAiMove = (data, pos) => {
    const timeAi = Math.floor(window.performance.now() - data.startAi)
    var nbSeconds = Math.floor(timeAi / 1000) % 1000
    var nbMilliSeconds = `${timeAi % 1000}`.padStart(3, '0')
    data.aiMoves += 1
    data.aiTotalTime += parseFloat(`${nbSeconds}.${nbMilliSeconds}`)
    document.getElementById('timer').innerHTML = `${nbSeconds},${nbMilliSeconds}s`
    document.getElementById('timerAverage').innerHTML = `${(data.aiTotalTime / data.aiMoves).toFixed(3)}s`
	launchPutStoneAnimation(data, pos, data.aiColor, 'human')
}

const canvasOnClick = (e, data) => {
    if (data.endGame || data.turn === 'ai' || data.ongoingAnimation)
        return
    var canvasOffsetWidth = data.canvas.offsetWidth
    var canvasOffsetHeight = data.canvas.offsetHeight
    var stone = getStonePosition(data, e, canvasOffsetWidth, canvasOffsetHeight)
    if (!data.board[stone.y][stone.x]){
		launchPutStoneAnimation(data, stone, data.playerColor, 'ai')
    }
}

const canvasOnMouseLeave = (e, data) => {
    if (data.endGame || data.turn === 'ai' || data.ongoingAnimation)
            return
        printBoard(data)
}

const initGame = (images, playerColor, firstPlayer) => {
    var data = {}
	data.socket = io(serverIp, {query : {page : 'human vs ai'}})
	data.captures = {ai : 0, player : 0}
	data.ongoingAnimation = 0
	data.aiLaunched = false
    data.WHITE = 1
    data.BLACK = 2
    data.DRAW = 3
    data.canvas = document.getElementById('canvas')
    data.ctx = data.canvas.getContext('2d')
    data.board = initBoard()
    data.turn = firstPlayer
    data.aiMoves = 0
    data.aiTotalTime = 0
    data.piecesOnBoard = 0
    data.playerColor = playerColor
    data.aiColor = playerColor === data.WHITE ? data.BLACK : data.WHITE
    data.images = images
    printBoard(data)
	data.socket.on('ai', pos => {receiveAiMove(data, pos)})
    if (data.turn === 'ai')
        launchAi(data)
    data.canvas.onmousemove = e => canvasOnMouseMove(e, data)
    data.canvas.onclick = e => canvasOnClick(e, data)
    data.canvas.onmouseleave = e => canvasOnMouseLeave(e, data)
}

export default initGame
