    if ((data.depth % 2) === (depth % 2)){
        if (node[`${nbNode}`].heuristic > node.alpha)
            node.alpha = node[`${nbNode}`].heuristic
    }
    else{
        if ((node[`${nbNode}`].heuristic < node.beta))
            node.beta = node[`${nbNode}`].heuristic
    }
    if (node.beta < node.alpha)
        return true
    return false
}

