import { freeBufferMemory, getBoard } from "./tools"

const getAlignmentValue = (neighbour1, neighbour2, sizeAlignment, data) => {
    if (sizeAlignment < 2 || (sizeAlignm(!neighbour1 && !neighbour2)))
        return 0
    if (neighbour1 && neighbour2){
        if (sizeAlignment === 2)
            return data.heuristic.align2Free
        else if (sizeAlignment === 3)
            return data.heuristic.align3Free
        else if (sizeAlignment === 4)
            return data.heuristic.align4Free
        return data.heuristic.align5
    }
    else{
        if (sizeAlignment === 2)
            return data.heuristic.align2
        if (sizeAlignment === 3)
            return data.heuristic.align3
        if (sizeAlignment === 4)
            return data.heuristic.align4
        return data.heuristic.align5
    }
}

const verticalAlignment = (board, node, data) => {
    var color = node.color
    var pos = {x : node.x, y : node.y}
    var neighbour1 = 0
    var neighbour2 = 0
    var sizeAlignment = 0
    while (pos.y >= 0 && board[pos.y][pos.x] === color){
        pos.y -= 1
    }
    if (pos.y >= 0 && !board[pos.y][pos.x])
        neighbour1 = 1
    pos.y += 1
    while (pos.y < 19 && board[pos.y][pos.x] === color){
        pos.y += 1
        sizeAlignment += 1
    }
    if (pos.y < 19 && !board[pos.y][pos.x])
        neighbour2 = 1
    return getAlignmentValue(neighbour1, neighbour2, sizeAlignment, data)
}

const horizontalAlignment = (board, node, data) => {
    var color = node.color
    var pos = {x : node.x, y : node.y}
    var neighbour1 = 0
    var neighbour2 = 0
    var sizeAlignment = 0
    while (pos.x >= 0 && board[pos.y][pos.x] === color){
        pos.x -= 1
    }
    if (pos.x >= 0 && !board[pos.y][pos.x])
        neighbour1 = 1
    pos.x += 1
    while (pos.x < 19 && board[pos.y][pos.x] === color){
        pos.x += 1
        sizeAlignment += 1
    }
    if (pos.x < 19 && !board[pos.y][pos.x])
        neighbour2 = 1
    return getAlignmentValue(neighbour1, neighbour2, sizeAlignment, data)
}

const diagonal1Alignment = (board, node, data) => {
    var color = node.color
    var pos = {x : node.x, y : node.y}
    var neighbour1 = 0
    var neighbour2 = 0
    var sizeAlignment = 0
    while (pos.x >= 0 && pos.y >= 0 && board[pos.y][pos.x] === color){
        pos.x -= 1
        pos.y -= 1
    }
    if (pos.x >= 0 && pos.y >= 0 && !board[pos.y][pos.x])
        neighbour1 = 1
    pos.x += 1
    pos.y += 1
    while (pos.x < 19 && pos.y < 19 && board[pos.y][pos.x] === color){
        pos.x += 1
        pos.y += 1
        sizeAlignment += 1
    }
    if (pos.x < 19 && pos.y < 19 && !board[pos.y][pos.x])
        neighbour2 = 1
    return getAlignmentValue(neighbour1, neighbour2, sizeAlignment, data)
}

const diagonal2Alignment = (board, node, data) => {
    var color = node.color
    var pos = {x : node.x, y : node.y}
    var neighbour1 = 0
    var neighbour2 = 0
    var sizeAlignment = 0
    while (pos.x < 19 && pos.y >= 0 && board[pos.y][pos.x] === color){
        pos.x += 1
        pos.y -= 1
    }
    if (pos.x < 19 && pos.y >= 0 && !board[pos.y][pos.x])
        neighbour1 = 1
    pos.x -= 1
    pos.y += 1
    while (pos.x >= 0 && pos.y < 19 && board[pos.y][pos.x] === color){
        pos.x -= 1
        pos.y += 1
        sizeAlignment += 1
    }
    if (pos.x >= 0 && pos.y < 19 && !board[pos.y][pos.x])
        neighbour2 = 1
    return getAlignmentValue(neighbour1, neighbour2, sizeAlignment, data)
}

const checkAlignments = (board, node, data) => {
    var h = 0
    h += verticalAlignment(board, node, data)
    h += horizontalAlignment(board, node, data)
    h += diagonal1Alignment(board, node, data)
    h += diagonal2Alignment(board, node, data)
    if (node.color === data.aiColor)
        return h / data.heuristicAiDiviser
    return -h
}

const getBlockingValue = (lengthBlocking, data, neighbour1, neighbour2) => {
    if (lengthBlocking === 0)
        return 0
    if (!neighbour1 && !neighbour2){
        if (lengthBlocking === 1)
            return data.heuristic.block1
        if (lengthBlocking === 2)
            return data.heuristic.block2
        if (lengthBlocking === 3)
            return data.heuristic.block3
    }
    else {
        if (lengthBlocking === 1)
            return data.heuristic.block1Free
        if (lengthBlocking === 2)
            return data.heuristic.block2Free
        if (lengthBlocking === 3)
            return data.heuristic.block3Free
    }
    return data.heuristic.block4
}

const verticalBlocking = (board, node, data) => {
    var color = node.color === data.BLACK ? data.WHITE : data.BLACK
    var lengthBlocking = 0
    var x = node.x
    var y = node.y
    var saveY = y
    var neighbour1 = 0
    var neighbour2 = 0
    while (y - 1 >= 0 && board[y - 1][x] === color){
        y--
        lengthBlocking += 1
    }
    if (y - 1 >= 0 && !board[y - 1][x] && board[y][x] !== node.color)
        neighbour1 = 1
    y = saveY
    while (y + 1 < 19 && board[y + 1][x] === color){
        y++
        lengthBlocking += 1
    }
    if (y + 1 < 19 && !board[y + 1][x] && board[y][x] !== node.color)
        neighbour2 = 1
    return getBlockingValue(lengthBlocking, data, neighbour1, neighbour2)
}

const horizontalBlocking = (board, node, data) => {
    var color = node.color === data.BLACK ? data.WHITE : data.BLACK
    var lengthBlocking = 0
    var x = node.x
    var y = node.y
    var saveX = x
    var neighbour1 = 0
    var neighbour2 = 0
