        neighbour1 = 1
    x = saveX
    while (x + 1 < 19 && board[y][x + 1] === color){
        x++
        lengthBlocking += 1
    }
    if (x + 1 < 19 && !board[y][x + 1] && board[y][x] !== node.color)
        neighbour2 = 1
    return getBlockingValue(lengthBlocking, data, neighbour1, neighbour2)
}

const diagonal1Blocking = (board, node, data) => {
    var color = node.color === data.BLACK ? data.WHITE : data.BLACK
    var lengthBlocking = 0
    var x = node.x
    var y = node.y
    var saveX = x
    var saveY = y
    var neighbour1 = 0
    var neighbour2 = 0
    while (x - 1 >= 0 && y - 1 >= 0 && board[y - 1][x - 1] === color){
        x--
        y--
        lengthBlocking += 1
    }
    if (x - 1 >= 0 && y - 1 >= 0 && !board[y - 1][x - 1] && board[y][x] !== node.color)
        neighbour1 = 1
    x = saveX
    y = saveY
    while (x + 1 < 19 && y + 1 < 19 && board[y + 1][x + 1] === color){
        x++
        y++
        lengthBlocking += 1
    }
    if (x + 1 < 19 && y + 1 < 19 && !board[y + 1][x + 1] && board[y][x] !== node.color)
        neighbour2 = 1
    return getBlockingValue(lengthBlocking, data, neighbour1, neighbour2)
}

const diagonal2Blocking = (board, node, data) => {
    var color = node.color === data.BLACK ? data.WHITE : data.BLACK
    var lengthBlocking = 0
    var x = node.x
    var y = node.y
    var saveX = x
    var saveY = y
    var neighbour1 = 0
    var neighbour2 = 0
    while (x + 1 >= 0 && y - 1 >= 0 && board[y - 1][x + 1] === color){
        x++
        y--
        lengthBlocking += 1
    }
    if (x + 1 >= 0 && y - 1 >= 0 && !board[y - 1][x + 1] && board[y][x] !== node.color)
        neighbour1 = 1
    x = saveX
    y = saveY
    while (x - 1 < 19 && y + 1 < 19 && board[y + 1][x - 1] === color){
        x--
        y++
        lengthBlocking += 1
    }
    if (x - 1 < 19 && y + 1 < 19 && !board[y + 1][x - 1] && board[y][x] !== node.color)
        neighbour2 = 1
    return getBlockingValue(lengthBlocking, data, neighbour1, neighbour2)
}

const checkBlockings = (board, node, data) => {
    var h = 0
    h += verticalBlocking(board, node, data)
    h += horizontalBlocking(board, node, data)
    h += diagonal1Blocking(board, node, data)
    h += diagonal2Blocking(board, node, data)
    if (node.color === data.aiColor)
        return h / data.heuristicAiDiviser
    return -h
}

// eslint-disable-next-line
