var nbConbinations = 0
var nbWins = 0

const checkStones = (x, y, board, orientation, sizeBoard, stones) => {
    var nb = 0
    var color = board[y][x]
	console.log(orientation)
    while (orientation === 'vertical' && y++ < sizeBoard && board[y - 1][x] === color)nb++
    while (orientation === 'horizontal' && x++ < sizeBoard && board[y][x - 1] === color){
		nb++
	}
    while (orientation === 'diagonal1' && x++ < sizeBoard && y++ < sizeBoard && board[y - 1][x - 1] === color)nb++
    while (orientation === 'diagonal2' && x-- >= 0 && y++ < sizeBoard && board[y - 1][x + 1] === color)nb++
    if (nb >= stones)
        return color
    return false
}

const checkAlignment = (x, y, board, sizeBoard, stones) => {
    var winner
    if (
        (winner = checkStones(x, y, board, 'vertical', sizeBoard, stones)) ||
        (winner = checkStones(x, y, board, 'horizontal', sizeBoard, stones)) ||
        (winner = checkStones(x, y, board, 'diagonal1', sizeBoard, stones)) ||
        (winner = checkStones(x, y, board, 'diagonal2', sizeBoard, stones))
    )
        return winner
    return false
}

const checkWinner = (board, sizeBoard, stones) => {
    var winner
    for (var y = 0; y < sizeBoard; y++){
        for (var x = 0; x < sizeBoard; x++){
            if (board[y][x] && (winner = checkAlignment(x, y, board, sizeBoard, stones)))
                return winner
        }
    }
    return false
}



const initArray = (sizeArray) => {
	var array = []
	for (var y = 0; y < sizeArray; y++){
		array[y] = []
		for (var x = 0; x < sizeArray; x++){
			array[y][x] = 0
		}
	}
	return array
}

const testAllConbinations = (array, sizeArray, stonesToPlace, stones) => {
	if (!stonesToPlace){
		console.log(nbConbinations)
		nbConbinations += 1
		if (checkWinner(array, sizeArray, stones)){
			nbWins += 1
		}
	}
	for (var y = 0; y < sizeArray; y++){
		for (var x = 0; x < sizeArray; x++){
			if (!array[y][x]){
				array[y][x] = 1
				testAllConbinations(array, sizeArray, stonesToPlace - 1, stones)
				array[y][x] = 0
			}
		}
	}
}

const test = () => {
	var sizeArray = 4
	var stones = 2
	var array = initArray(sizeArray)
	testAllConbinations(array, sizeArray, stones, stones)
	console.log(nbConbinations)
	console.log(nbWins)
}

export default test
