const checkStones = (x, y, board, playerColor, orientation) => {
    var nb = 0
    var color = board[y][x]
    while (orientation === 'vertical' && y++ < 19 && board[y - 1][x] === color)nb++
    while (orientation === 'horizontal' && x++ < 19 && board[y][x - 1] === color)nb++
    while (orientation === 'diagonal1' && x++ < 19 && y++ < 19 && board[y - 1][x - 1] === color)nb++
    while (orientation === 'diagonal2' && x-- >= 0 && y++ < 19 && board[y - 1][x + 1] === color)nb++
    if (nb >= 5)
        return color === playerColor ? 'human' : 'ia'
    return false
}

const checkAlignment = (x, y, board, playerColor) => {
    var winner
    if (
        (winner = checkStones(x, y, board, playerColor, 'vertical')) ||
        (winner = checkStones(x, y, board, playerColor, 'horizontal')) ||
        (winner = checkStones(x, y, board, playerColor, 'diagonal1')) ||
        (winner = checkStones(x, y, board, playerColor, 'diagonal2'))
    )
        return winner
    return false
}

const checkWinner = (data, playerColor) => {
    var winner
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if (
                (data.board[y][x]) && 
                (winner = checkAlignment(x, y, data.board, playerColor))
            )
                return winner
        }
    }
    return false
}

const checkEndGame = (data, playerColor) => {
    var winner = checkWinner(data, playerColor)
    if (winner)
        return winner
    if (data.piecesOnBoard === 19*19)
        return 'draw'
    return false
}

export default checkEndGame
