// FAIRE DES STRUCTURES POUR LES DIFFÉRENTES COMPOSITIONS DU PLATEAU

var data

const getBoard = (node) => {
    var res = []
    for (var y = 0; y < 19; y++){
        res[y] = []
        for (var x = 0; x < 19; x++){
            res[y][x] = data.board
        }
    }
    while (node.parent){
        res[node.y][node.x] = node.color
        node = node.parent
    }

    return res
}

const noStones = (node, x, y) => {
    while (node.parent){
        if (node.x === x && node.y === y)
            return false
        node = node.parent
    }
    return true
}

const getHeuristic = (node) => {
    var board = getBoard(node)
    return 10
}

const getBestMove = (node, extrem, depth) => {
    var nbNode = 0
    var bestHeuristic = false
    while (node[`${nbNode}`]){
        if (
            bestHeuristic === false ||
            (extrem === 'min' && node[`${nbNode}`].heuristic < bestHeuristic) ||
            (extrem === 'max' && node[`${nbNode}`].heuristic > bestHeuristic)
        ){
            bestHeuristic = node[`${nbNode}`].heuristic
        }
        nbNode++
    }
    if (depth !== data.depth)
        return {heuristic : bestHeuristic}
    nbNode = 0
    var bestMove = []
    while (node[`${nbNode}`]){
        if (node[`${nbNode}`].heuristic === bestHeuristic){
            bestMove.push({
                x : node[`${nbNode}`].x,
                y : node[`${nbNode}`].y
            })
        }
        nbNode++
    }
    var moveChoosen = bestMove.length === 1 ? 0 : getRandomInt(bestMove.length)
    return {
        heuristic : bestHeuristic,
        x : bestMove[moveChoosen].x,
        y : bestMove[moveChoosen].y
    }
}

const minmax = (node, depth) => {
    var nbNode = 0
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if (!data.board[y][x] && noStones(node, x, y)){
                node[`${nbNode}`] = {
                    x, y,
                    parent : node,
                    color : (data.depth % 2) === (depth % 2) ? data.iaColor : data.playerColor
                }
                if (depth > 1){
                    minmax(node, depth - 1)
                }
                else
                    node[`${nbNode}`].heuristic = getHeuristic(node)
                nbNode++
            }
        }
    }
    var bestMove
    if ((data.depth % 2) !== (depth % 2))
        bestMove = getBestMove(node, 'min', depth)
    else
        bestMove = getBestMove(node, 'max', depth)
    if (depth === data.depth)
        return {x : bestMove.x, y : bestMove.y}
    node.heuristic = bestMove.heuristic
}


const blockCase = (x, y, board, offset) => {
    var pos = []
    for (var c = 0; c < offset; c++){
        pos = []
        pos.x = x - (c + 1)
        pos.y = y - (c + 1)
        for (var i = 0; i < 3 + c * 2 - 1; i++){
            if (board[pos.y][pos.x])
                return false 
            pos.x++
        }

    }
    return true
}

const blockBoard = (board, offset) => {
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if (blockCase(x, y, board, offset))
                board[y][x] = 1
        }
    }
}

const getStartBoard = board => {
    var res = []
    for (var y = 0; y < 19; y++){
        res[y] = []
        for (var x = 0; x < 19; x++){
            res[y][x] = board[y][x]
        }
    }
    blockBoard(res, 2)
}

const initData = (board, playerColor) => {
    data = {}
    data.playerColor = playerColor
    data.iaColor = playerColor === 'white' ? 'black' : 'white'
    data.board = getStartBoard(board)
    data.depth = 1
}

const ia = (board, playerColor) => {
    // return (playRandom(data, playerColor))
    // for (var c = 0; c < 500000000; c++);
    initData(board, playerColor)
    var tree = {}
    return(minmax(tree, data.depth))
}

