const getBoard = (putStones, data) => {
    for (var c = 0; c < putStones.length; c++){
        data.bufferMemory[putStones[c][1]][putStones[c][0]] = c % 2 ? playerColor : iaColor
    }
}

const freeBufferMemory = (putStones, bufferMemory) => {
    while (node.parent){
        bufferMemory[node.y][node.x] = 0
        node = node.parent
    }
}

module.exports = {
    getBoard,
    freeBufferMemory
}
