const checkVerticalAlignment = (x, y, board) => {
    var nb = 0
    var color = board[y][x]
    while (y < 19 && nb < 5 && board[y][x] === color){
        y++
        nb++
    }
    if (nb === 5)
        return color
    return false
}

const checkAlignment = (x, y, board) => {
    var winner
    if ((winner = checkVerticalAlignment(x, y, board)))
        return winner
    return false
}

const checkWinner = (data, playerColor) => {
    var winner
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if ((winner = checkAlignment(x, y, data.board, playerColor)))
                return winner
        }
    }
    return false
}

const checkEndGame = (data, playerColor) => {
    var winner = checkWinner(data, playerColor)
    if (winner)
        return winner
    if (data.piecesOnBoard === 19*19)
        return 'draw'
    return false
}

export default checkEndGame
