const fillBoard = (putStones, data) => {
    for (var c = 0; c < putStones.length; c++){
        data.bufferMemory[putStones[c][1]][putStones[c][0]] = c % 2 ? playerColor : iaColor
    }
}

const freeBufferMemory = (putStones, data) => {
    for (var c = 0; c < putStones.length; c++){
        data.bufferMemory[putStones[c][1]][putStones[c][0]] = 0
    }
}

module.exports = {
    getBoard,
    freeBufferMemory
}
