const checkAlignment = (x, y, data, xIncrement, yIncrement) => {
	var sizeAlignment = 0
	var color = data.board[y][x]
	var ennemyColor
	if (color == data.BLACK)
		ennemyColor = data.WHITE
	else
		ennemyColor = data.BLACK
	while (x >= 0 && x < 19 && y >= 0 && y < 19 && data.board[y][x] === color
		&& !capturePresent(x, y, ennemyColor, color, xCaptureIncrement, yCaptureIncrement)){
		sizeAlignment += 1;
		x += xIncrement
		y += yIncrement
	}
	if (sizeAlignment >= 5){
		return color
	}
	return false
}

const checkAlignments = (x, y, data) => {
    var winner
    if (
        (winner = checkAlignment(x, y, data, 0, 1)) ||
        (winner = checkAlignment(x, y, data, 1, 0)) ||
        (winner = checkAlignment(x, y, data, 1, 1)) ||
        (winner = checkAlignment(x, y, data, -1, 1))
    )
        return winner
    return false
}

const checkBoard = data => {
    var winner
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if (data.board[y][x] && (winner = checkAlignments(x, y, data)))
                return winner;
        }
    }
	return false
}

const checkEndGame = (data) => {
    if (data.piecesOnBoard === 361)
        return data.DRAW
	if (data.captures.white >= 10)
		return data.WHITE
	if (data.captures.black >= 10)
		return data.BLACK
	return checkBoard(data)
}

export default checkEndGame
