const inBoard = pos => {
	if (pos.x >= 0 && pos.x < 19 && pos.y >= 0 && pos.y < 19)
		return true
	return false
}

const color = (board, pos) => {
	return board[pos.y][pos.x]
}

const capturePresent = (board, x, y, color, ennemyColor, xIncrement, yIncrement) => {
	var pos = [
		{x : x + xIncrement * 2, y : y + yIncrement * 2},
		{x : x + xIncrement, y : y + yIncrement},
		{x, y},
		{x : x - xIncrement, y : y - yIncrement},
		{x : x - xIncrement * 2, y : y - yIncrement * 2}
	]
	if (inBoard(pos[0]) && color(pos[0]) === color &&
		inBoard(pos[1]) && color(pos[1]) === ennemyColor &&
		inBoard(pos[3]) && color(pos[3]) === color)
		return true

}

const checkAlignment = (x, y, data, xIncrement, yIncrement, xCaptureIncrement, yCaptureIncrement) => {
	var sizeAlignment = 0
	var color = data.board[y][x]
	var ennemyColor
	if (color == data.BLACK)
		ennemyColor = data.WHITE
	else
		ennemyColor = data.BLACK
	while (x >= 0 && x < 19 && y >= 0 && y < 19 && data.board[y][x] === color
		&& !capturePresent(data.board, x, y, ennemyColor, color, xCaptureIncrement, yCaptureIncrement)){
		sizeAlignment += 1;
		x += xIncrement
		y += yIncrement
	}
	if (sizeAlignment >= 5){
		return color
	}
	return false
}

const checkAlignments = (x, y, data) => {
    var winner
    if (
        (winner = checkAlignment(x, y, data, 0, 1, -1, 0)) ||
        (winner = checkAlignment(x, y, data, 1, 0)) ||
        (winner = checkAlignment(x, y, data, 1, 1)) ||
        (winner = checkAlignment(x, y, data, -1, 1))
    )
        return winner
    return false
}

const checkBoard = data => {
    var winner
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if (data.board[y][x] && (winner = checkAlignments(x, y, data)))
                return winner;
        }
    }
	return false
}

const checkEndGame = (data) => {
    if (data.piecesOnBoard === 361)
        return data.DRAW
	if (data.captures.white >= 10)
		return data.WHITE
	if (data.captures.black >= 10)
		return data.BLACK
	return checkBoard(data)
}

export default checkEndGame
