const inBoard = pos => {
	if (pos.x >= 0 && pos.x < 19 && pos.y >= 0 && pos.y < 19)
		return true
	return false
}

const getColor = (board, pos) => {
	return board[pos.y][pos.x]
}

const possibleCapture = (board, x, y, color, ennemyColor, increment) => {
	var pos
	for (var c = 0; c < 3; c += 1){
		pos = [
			{x : x + increment[c][0] * 2, y : y + increment[c][1] * 2},
			{x : x + increment[c][0], y : y + increment[c][1]},
			{x, y},
			{x : x - increment[c][0], y : y - increment[c][1]},
			{x : x - increment[c][0] * 2, y : y - increment[c][1] * 2}
		]
		if (inBoard(pos[0]) && getColor(board, pos[0]) === color &&
			inBoard(pos[1]) && getColor(board, pos[1]) === ennemyColor &&
			inBoard(pos[3]) && getColor(board, pos[3]) === 0)
			return true
		if (inBoard(pos[1]) && getColor(board, pos[1]) === color &&
			inBoard(pos[3]) && getColor(board, pos[3]) === ennemyColor &&
			inBoard(pos[4]) && getColor(board, pos[4]) === 0)
			return true
		if (inBoard(pos[1]) && getColor(board, pos[1]) === 0 &&
			inBoard(pos[3]) && getColor(board, pos[3]) === ennemyColor &&
			inBoard(pos[4]) && getColor(board, pos[4]) === color)
			return true
		if (inBoard(pos[0]) && getColor(board, pos[0]) === 0 &&
			inBoard(pos[1]) && getColor(board, pos[1]) === ennemyColor &&
			inBoard(pos[3]) && getColor(board, pos[3]) === color)
			return true
	}
	return false
}

const checkAlignment = (x, y, data, xIncrement, yIncrement, captureIncrement) => {
	var sizeAlignment = 0
	var color = data.board[y][x]
	var ennemyColor
	if (color == data.BLACK)
		ennemyColor = data.WHITE
	else
		ennemyColor = data.BLACK
	while (x >= 0 && x < 19 && y >= 0 && y < 19 && data.board[y][x] === color
		&& !possibleCapture(data.board, x, y, ennemyColor, color, captureIncrement)){
		sizeAlignment += 1;
		x += xIncrement
		y += yIncrement
	}
	if (sizeAlignment >= 6){
		return color
	}
	return false
}

const checkAlignments = (x, y, data) => {
    var winner
    if (
        (winner = checkAlignment(x, y, data, 0, 1, [[-1, 0], [-1, -1], [-1, 1]])) ||
        (winner = checkAlignment(x, y, data, 1, 0, [[0, -1], [-1, -1], [1, -1]])) ||
        (winner = checkAlignment(x, y, data, 1, 1, [[-1, 1], [-1, 0], [0, 1]])) ||
        (winner = checkAlignment(x, y, data, -1, 1, [[-1, -1], [0, -1], [-1, 0]]))
    )
        return winner
    return false
}

const checkBoard = data => {
    var winner
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if (data.board[y][x] && (winner = checkAlignments(x, y, data)))
                return winner;
        }
    }
	return false
}

const checkEndGame = (data) => {
    if (data.piecesOnBoard === 361)
        return data.DRAW
	if (data.captures.white >= 10)
		return data.WHITE
	if (data.captures.black >= 10)
		return data.BLACK
	return checkBoard(data)
}

export default checkEndGame
