const noStones = (node, x, y) => {
    while (node.parent){
        if (node.x === x && node.y === y)
            return false
        node = node.parent
    }
    return true
}

const getBoard = (board, node) => {
    var res = []
    for (var y = 0; y < 19; y++){
        res[y] = []
        for (var x = 0; x < 19; x++){
            res[y][x] = board
        }
    }
    while (node.parent){
        res[node.y][node.x] = node.color
        node = node.parent
    }
    return res
}

const getBestMove = (node) => {
    var board = getBoard(data.board, node)
    var nbNode = 0
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            node[`${nbNode}`] = {
                x, y,
                parent : node,
                color : depth % 2 === 0 ? 'black' : 'white',
                heuristic : getHeuristic
            }
        }
    }
}

const minmax = (node, depth) => {
    if (depth === 1)
        getBestMove(node)
    else{
        var nbNode
        var board = getBoard(data.board, node)
        for (var y = 0; y < 19; y++){
            for (var x = 0; x < 19; x++){
                if (!data.board[y][x] && noStones(node, x, y)){
                    node[`${nbNode}`] = {
                        x, y,
                        parent : node,
                        color : depth % 2 === 0 ? 'black' : 'white'
                    }
                    minmax(node, depth)
                    nbNode++
                }
            }
        }
    }

}

