        return
    for (var c = 0; c < data.iaPath.length; c++){
        if (!data.board[data.iaPath[c].y][data.iaPath[c].x] && noStones(node, data.iaPath[c].x, data.iaPath[c].y)){
            node[nbNode] = getChild(data.iaPath[c], node, color)
            if (depth > 1)
                minmax(node[nbNode], depth - 1)
            else
                node[nbNode].heuristic = getHeuristic(node[nbNode])
            uploadHeuristic(node, node[nbNode], depth)
            if (getAlphaBetaPruning(node, depth, nbNode))
                break
            // node[nbNode] = undefined // WORKS BUT USELESS FOR THE MOMENT
            nbNode++
        }
    }
}

