import getRandomInt from "../../tools/getRandomInt"

const createTree = (depth, nbChilds) => {
    var node = {}
    var child = 0
    node.var = getRandomInt(1000)
    while (child < nbChilds){
        if (depth > 0)
            node[child] = createTree(depth - 1, nbChilds)
        child += 1
    }
    return node
}

const printTree = (tree, indentation) => {
    var nbNode = 0
    var prefix = ''
    for (var c = 0; c < indentation; c++){
        prefix += '----'
    }
    console.log(`${prefix}(${tree.var})`)
    while (tree[nbNode]){
        printTree(tree[nbNode], indentation + 1)
        nbNode++
    }
}

const drawTree = (tree, depth) => {
    const data = {}
    data.canvas = document.createElement('CANVAS');
    data.ctx = data.canvas.getContext('2d')
    // var myTree = createTree(3, 2)
    // printTree(myTree, 0)
    canvas.width = 100
    canvas.height = 100
    ctx.rect(0, 0, 50, 50)
    canvas.width += 100
    canvas.height += 100
    ctx.rect(100, 100, 50, 50)
    var image = new Image()
    image.src = data.canvas.toDataURL()
    image.onload = () => {
        document.getElementById('board').appendChild()
    }
}

export default drawTree
