var nbConbinations = 0

const initArray = (sizeArray) => {
	var array = []
	for (var y = 0; y < sizeArray; y++){
		array[y] = []
		for (var x = 0; x < sizeArray; x++){
			array[y][x] = 0
		}
	}
	return array
}

const testAllConbinations = (array, sizeArray, stones) => {
	if (!stones)
		nbConbinations += 1
	for (var y = 0; y < sizeArray; y++){
		for (var x = 0; x < sizeArray; x++){
			if (!array[y][x]){
				array[y][x] = 1
				testAllConbinations(array, sizeArray, stones - 1)
				array[y][x] = 0
			}
		}
	}
}

const test = () => {
	var sizeArray = 10
	var stones = 5
	var array = initArray(sizeArray)
	testAllConbinations(array, sizeArray, stones)
	console.log(nbConbinations)
}

export default test
