const singleStoneHeuristic = (board, node, data) => {
    var h = 10
    var free = 1
    if (!board[node.y - 1][node.x])
        free +=1
    if (!board[node.y][node.x + 1])
        free +=1
    if (!board[node.y + 1][node.x])
        free +=1
    if (!board[node.y][node.x - 1])
        free +=1
    h = h * free
    if (board[node.y][node.x] === data.iaColor)
        return h
    return -h
}

const getHeuristic = (node, data) => {
    const start = window.performance.now()
    var board = getBoard(node, data.board)
    var h1 = 0
    var h2 = 0
    while (node.parent){
        h1 += singleStoneHeuristic(board, node, data)
        // h2 += alignmentHeuristic(board, node)
        node = node.parent
    }
    const end = window.performance.now()
    data.heuristicTime += end - start
    return h1 + h2
}
