const checkStones = (x, y, board, orientation) => {
    var nb = 0
    var color = board[y][x]
    while (orientation === 'vertical' && y++ < 19 && board[y - 1][x] === color)nb++
    while (orientation === 'horizontal' && x++ < 19 && board[y][x - 1] === color)nb++
    while (orientation === 'diagonal1' && x++ < 19 && y++ < 19 && board[y - 1][x - 1] === color)nb++
    while (orientation === 'diagonal2' && x-- >= 0 && y++ < 19 && board[y - 1][x + 1] === color)nb++
    if (nb >= 5)
        return color
    return false
}

const checkAlignment = (x, y, board, xIncrement, yIncrement) => {
	var sizeAlignment = 0
	var color = board[y][x]
	while (x >= 0 && x < 19 && y >= 0 && y < 19 && board[y][x] === color){
		sizeAlignment += 1;
		x += xIncrement
		y += yIncrement
	}
	if (sizeAlignment >= 5){
		return color
	}
	return false
}

const checkAlignments = (x, y, board) => {
    var winner
    if (
        (winner = checkAlignment(x, y, board, 0, 1)) ||
        (winner = checkAlignment(x, y, board, 1, 0)) ||
        (winner = checkAlignment(x, y, board, 1, 1)) ||
        (winner = checkAlignment(x, y, board, -1, 1))
    )
        return winner
    return false
}

const checkBoard = (board) => {
    var winner
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if (board[y][x] && (winner = checkAlignments(x, y, board)))
                return winner;
        }
    }
	return false
}

const checkEndGame = (data) => {
    if (data.piecesOnBoard === 361)
        return data.DRAW
	if (data.captures.white >= 10)
		return data.WHITE
	if (data.captures.black >= 10)
		return data.BLACK
	return checkBoard(data.board)
}

export default checkEndGame
