import { useEffect, useState } from "react"
import blackPiece from '../images/blackPiece.png'
import gomokuBoard from '../images/gomokuBoard.png'
import whitePiece from '../images/whitePiece.png'

const loadImages = () => {
    return new Promise((resolve, reject) => {
        const imagesToLoad = [blackPiece, gomokuBoard, whitePiece]
        var imagesLoaded = 0
        var images = []
        for (var c = 0; c < imagesToLoad.length; c++){
            images[c] = new Image()
            images[c].src = imagesToLoad[c]
            images[c].onload = () => {
                imagesLoaded += 1
                if (imagesLoaded === imagesToLoad.length)
                    resolve(images)
            }
        }
    })
}

const write = images => {
    const data = {}
    data.canvas = document.getElementById('canvas')
    data.ctx = data.canvas.getContext('2d')

}

const GenerateText = () => {
    const [images, setImages] = useState(false)


    useEffect(() => {
        console.log('Bonjour')
        if (!images){
            loadImages().then(images => {
                setImagesLoaded(true)
            })
        }
        else
            write(images)
    })

    loadImages().then(images => {
        setImagesLoaded(true)
        write(images)
    })

    if (images)
        return (
            <div>
                <canvas id='canvas' width="500" height="500">
                    Impossible to load the canvas...
                </canvas>
            </div>
        )
    return (
        <div>
            CHARGEMENT...
        </div>
    )
}

export default GenerateText
