import ia from '../../tools/ia/ia'
import checkEndGame from './checkEndGame'

const printBackground = (data) => {
    data.ctx.fillStyle = '#a6683e'
    data.ctx.fillRect(0, 0, data.canvas.width, data.canvas.height)
    for (var y = 0; y < 18; y++){
        for (var x = 0; x < 18; x++)
            data.ctx.drawImage(
                data.images.gomokuBoard,
                x * data.images.gomokuBoard.width + data.images.blackPiece.width / 2,
                y * data.images.gomokuBoard.height + data.images.blackPiece.width / 2
            )
    }
}

const initBoard = () => {
    var board = []
    for (var y = 0; y < 19; y++){
        board[y] = []
        for (var x = 0; x < 19; x++){
            board[y][x] = 0
        }
    }
    return board
}

const printStones = (data) => {
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if (data.board[y][x]){
                var stone = data.board[y][x] === data.BLACK ? data.images.blackPiece : data.images.whitePiece
                data.ctx.drawImage(
                    stone,
                    data.images.gomokuBoard.width * x,
                    data.images.gomokuBoard.height * y
                )
            }
        }
    }
}

const printBlockedBoard = (data) => {
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if (data.board[y][x]){
                if (data.board[y][x] === 9)
                data.ctx.drawImage(
                    data.images.forbidden,
                    data.images.gomokuBoard.width * x,
                    data.images.gomokuBoard.height * y
                )
            }
        }
    }
}

const printBoard = (data) => {
    printBackground(data)
    printStones(data)
    printBlockedBoard(data)
}

const getStonePosition = (data, e, canvasOffsetWidth, canvasOffsetHeight) => {
    return {
        x : Math.floor(((e.offsetX * data.canvas.width) / canvasOffsetWidth) / (data.canvas.width / 19)),
        y : Math.floor(((e.offsetY * data.canvas.height) / canvasOffsetHeight) / (data.canvas.height / 19))
    }
}

const canvasOnMouseMove = (e, data) => {
    if (data.endGame || data.turn === 'ia')
        return
    var canvasOffsetWidth = data.canvas.offsetWidth
    var canvasOffsetHeight = data.canvas.offsetHeight
    var stone = getStonePosition(data, e, canvasOffsetWidth, canvasOffsetHeight)
    if (stone.y === 19)
        return
    if (!data.board[stone.y][stone.x]){
        printBoard(data)
        data.ctx.globalAlpha = 0.5
        data.ctx.drawImage(
            data.playerColor === data.WHITE ? data.images.whitePiece : data.images.blackPiece,
            data.images.gomokuBoard.width * stone.x,
            data.images.gomokuBoard.height * stone.y
        )
        data.ctx.globalAlpha = 1
    }
    else
        printBoard(data)
}

const setEndGame = (data) => {
    var params = {}
    params[data.DRAW] = {
        text : 'END OF GAME, NO WINNER',
        color : 'orange'
    }
    params[data.playerColor] = {
        text : 'YOU WON THE GAME !',
        color : 'green'
    }
    params[data.iaColor] = {
        text : 'YOU LOST THE GAME',
        color : 'red'
    }
    document.getElementById('endGame').style.color = params[data.endGame].color
    document.getElementById('endGame').innerHTML = params[data.endGame].text
    document.getElementById('newGame').style.display = 'block'
}

const launchIa = (data) => {
    const startIa = window.performance.now()
    var pos = ia(data)
    const timeIa = Math.floor(window.performance.now() - startIa)
    data.board[pos.y][pos.x] = data.playerColor === data.BLACK ? data.WHITE : data.BLACK
    data.piecesOnBoard += 1
    var nbSeconds = Math.floor(timeIa / 1000) % 1000
    var nbMilliSeconds = `${timeIa % 1000}`.padStart(3, '0')
    data.iaMoves += 1
    data.iaTotalTime += parseFloat(`${nbSeconds}.${nbMilliSeconds}`)
    document.getElementById('timer').innerHTML = `${nbSeconds},${nbMilliSeconds}s`
    document.getElementById('timerAverage').innerHTML = `${(data.iaTotalTime / data.iaMoves).toFixed(3)}s`
    printBoard(data)
    if ((data.endGame = checkEndGame(data, data.playerColor))){
        setEndGame(data)
        return
    }
    data.turn = 'human'
}

const canvasOnClick = (e, data) => {
    if (data.endGame || data.turn === 'ia')
        return
    var canvasOffsetWidth = data.canvas.offsetWidth
    var canvasOffsetHeight = data.canvas.offsetHeight
    var stone = getStonePosition(data, e, canvasOffsetWidth, canvasOffsetHeight)
    if (!data.board[stone.y][stone.x]){
        data.board[stone.y][stone.x] = data.playerColor
        data.piecesOnBoard += 1
        printBoard(data)
        if ((data.endGame = checkEndGame(data, data.playerColor))){
            setEndGame(data)
            return
        }
        data.turn = 'ia'
        launchIa(data, data.images, data.playerColor)
    }
}

const canvasOnMouseLeave = (e, data) => {
    if (data.endGame || data.turn === 'ia')
            return
        printBoard(data)
}

const initGame = (images, playerColor, firstPlayer) => {
    var data = {}
    data.WHITE = 1
    data.BLACK = 2
    data.DRAW = 3
    data.canvas = document.getElementById('canvas')
    data.ctx = data.canvas.getContext('2d')
    data.board = initBoard()
    data.turn = firstPlayer
    data.iaMoves = 0
    data.iaTotalTime = 0
    data.piecesOnBoard = 0
    data.playerColor = playerColor
    data.iaColor = playerColor === data.WHITE ? data.BLACK : data.WHITE
    data.images = images
    printBoard(data)
    if (data.turn === 'ia')
        launchIa(data)
    data.canvas.onmousemove = e => canvasOnMouseMove(e, data)
    data.canvas.onclick = e => canvasOnClick(e, data)
    data.canvas.onmouseleave = e => canvasOnMouseLeave(e, data)
}

export default initGame
