    data.aiMoves = 0
    data.aiTimePlay = 0
	data.aiColor = data.firstPlayerColor
	data.playerColor = data.aiColor === data.BLACK ? data.WHITE : data.BLACK
	data.canvas.onclick = e => canvasOnClick(e, data)
	// data.canvas.onclick = e => launchAi(data)
	getSuggestion(data)
	launchAi(data)
}


