import { useEffect, useState } from 'react'
import Header from '../../tools/header.js'
import Footer from '../../tools/footer.js'
import styles from './humanvsia.module.css'
import initGame from './initGame'

const StartGame = () => {
    return (
        <div className={styles.canvasContainer}>
            <canvas
                id='canvas'
                width={`${images.gomokuBoard.width * 18 + images.blackPiece.width}`}
                height={`${images.gomokuBoard.height * 18 + images.blackPiece.height}`}
                className={styles.canvas}>
            </canvas>
        </div>
    )
}

const Game = ({images}) => {
    const [state, setState] = useState('whobegin')

    useEffect(() => {
        if (state === 'game')
            var data = initGame(images)
    })

    if (state === 'whobegin')
        return <WhoBegin/>
    else if (state === 'chooseyourcolor')
        return <ChooseYourColor/>
    else if (state === 'game')
        return <StartGame images={images}/>

}

const Humanvsia = ({images}) => {
    return (
        <div>
            <Header/>
            <Game images={images}/>
            <Footer/>
        </div>
    )
}

export default Humanvsia
