const getBoard = (node, board) => {
    var res = []
    for (var y = 0; y < 19; y++){
        res[y] = []
        for (var x = 0; x < 19; x++){
            res[y][x] = board[y][x]
        }
    }
    while (node.parent){
        res[node.y][node.x] = node.color
        node = node.parent
    }
    return res
}

module.exports = {
    getBoard
}
