const singleStonesHeuristic = board => {
    var heuristic = 0
    var localHeuristic
    var freeNeighbour
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if (board[y][x]){
                localHeuristic = 10
                freeNeighbour = 1
                if (y - 1 >= 0 && !board[y - 1][x])
                    freeNeighbour += 1
                if (y - 1 >= 0 && x + 1 < 19 && !board[y - 1][x + 1])
                    freeNeighbour += 1
