                if (y + 1 < 19 && x - 1 >= 0 && !board[y + 1][x - 1])
                    freeNeighbour += 1
                if (x - 1 >= 0 && !board[y][x - 1])
                    freeNeighbour += 1
                if (x - 1 >= 0 && y - 1 >= 0 && !board[y - 1][x - 1])
                localHeuristic *= freeNeighbour
                if (localHeuristic > 10)
