import { getBoard } from "./tools"

const singleStoneHeuristic = (board, node, data) => {
    var h = 10
    var free = 1
    if (node.y - 1 >= 0 && !board[node.y - 1][node.x])
        free +=1
    if (node.x + 1 < 19 && !board[node.y][node.x + 1])
        free +=1
    if (node.y + 1 < 19 && !board[node.y + 1][node.x])
        free +=1
    if (node.x - 1 >= 0 && !board[node.y][node.x - 1])
        free +=1
    h *= free
    if (board[node.y][node.x] === data.iaColor)
        return h
    return -h
}

const verticalAlignment = (board, node) => {
    var color = node.color
    var pos = {x : node.x, y : node.y}
    var neighbour1 = 0
    var neighbour2 = 0
    var sizeAlignment = 0
    while (pos.y >= 0 && board[pos.y][pos.x] === color){
        pos.y -= 1
    }
    if (pos.y >= 0 && !board[pos.y][pos.x])
        neighbour1 = 1
    pos.y += 1
    while (pos.y < 19 && board[pos.y][pos.x] === color){
        pos.y += 1
        sizeAlignment += 1
    }
    if (pos.y < 19 && !board[pos.y][pos.x])
        neighbour2 = 1
    if (!neighbour1 && !neighbour2)
        return 0
    return Math.pow(10, sizeAlignment) * (neighbour1 + neighbour2 + 1)
}

const horizontalAlignment = (board, node) => {
    var color = node.color
    var pos = {x : node.x, y : node.y}
    var neighbour1 = 0
    var neighbour2 = 0
    var sizeAlignment = 0
    while (pos.x >= 0 && board[pos.y][pos.x] === color){
        pos.x -= 1
    }
    if (pos.x >= 0 && !board[pos.y][pos.x])
        neighbour1 = 1
    pos.x += 1
    while (pos.x < 19 && board[pos.y][pos.x] === color){
        pos.x += 1
        sizeAlignment += 1
    }
    if (pos.x < 19 && !board[pos.y][pos.x])
        neighbour2 = 1
    if (!neighbour1 && !neighbour2)
        return 0
    return Math.pow(10, sizeAlignment) * (neighbour1 + neighbour2 + 1)
}

const diagonalAlignment = (board, node) => {
    var color = node.color
    var pos = {x : node.x, y : node.y}
    var neighbour1 = 0
    var neighbour2 = 0
    var sizeAlignment = 0
    while (pos.x >= 0 && board[pos.y][pos.x] === color){
        pos.x -= 1
    }
    if (pos.x >= 0 && !board[pos.y][pos.x])
        neighbour1 = 1
    pos.x += 1
    while (pos.x < 19 && board[pos.y][pos.x] === color){
        pos.x += 1
        sizeAlignment += 1
    }
    if (pos.x < 19 && !board[pos.y][pos.x])
        neighbour2 = 1
    if (!neighbour1 && !neighbour2)
        return 0
    return Math.pow(10, sizeAlignment) * (neighbour1 + neighbour2 + 1)
}

const alignmentHeuristic = (board, node, data) => {
    var h = 0
    h += verticalAlignment(board, node)
    h += horizontalAlignment(board, node)
    h += diagonal1Alignment(board, node)
    if (board[node.y][node.x] === data.iaColor)
        return h
    return -h
}

const getHeuristic = (node, data) => {
    const start = window.performance.now()
    var board = getBoard(node, data.board)
    var h1 = 0
    var h2 = 0
    while (node.parent){
        h1 += singleStoneHeuristic(board, node, data)
        h2 += alignmentHeuristic(board, node, data)
        node = node.parent
    }
    const end = window.performance.now()
    data.heuristicTime += end - start
    return h1 + h2
}

export default getHeuristic
