const getBestHeuristic = (node, extrem) => {
    var nbNode = 0
    var bestHeuristic = false
    while (node[nbNode]){
        if (
            bestHeuristic === false ||
            (extrem === data.MIN && node[nbNode].heuristic < bestHeuristic) ||
            (extrem === data.MAX && node[nbNode].heuristic > bestHeuristic)
        ){
            bestHeuristic = node[nbNode].heuristic
        }
        nbNode++
    }
    return bestHeuristic
}

const randomChoice = (node, bestHeuristic) => {
    var nbNode = 0
    var bestMove = []
    while (node[nbNode]){
        if (node[nbNode].heuristic === bestHeuristic){
            bestMove.push({
                x : node[nbNode].x,
                y : node[nbNode].y,
            })
        }
        nbNode++
    }
    var moveChoosen = bestMove.length === 1 ? 0 : getRandomInt(bestMove.length)
    return {
        heuristic : bestHeuristic,
        x : bestMove[moveChoosen].x,
        y : bestMove[moveChoosen].y
    }
}

