const getBestMove = (node, extrem, depth) => {
    var bestHeuristic = getBestHeuristic(node, extrem)
    // printSameNodes(node, bestHeuristic) // TMP
    if (depth !== data.depth)
        return {heuristic : bestHeuristic}
    return randomChoice(node, bestHeuristic)

}

const getAlphaBetaPruning = (node, depth, nbNode) => {
    if ((data.depth % 2) === (depth % 2)){
        if (node[nbNode].heuristic > node.alpha)
            node.alpha = node[nbNode].heuristic
    }
    else{
        if ((node[nbNode].heuristic < node.beta))
            node.beta = node[nbNode].heuristic
    }
    if (node.beta <= node.alpha)
        return true
    return false
}

const increment = (pos) => {
    pos.x++
    if (pos.x === 19){
        pos.x = 0
        pos.y++
    }
    if (pos.y === 19)
        return false
    return true
}

const getChild = (pos, node, depth, color) => {
    return {
        x : pos.x, y : pos.y,
        parent : node,
        color,
        alpha : node.alpha, beta : node.beta // A SUPPRIMER POUR LA DERNIERE LIGNE DE L'ARBRE
    }
}

const uploadHeuristic = (depth, node) => {
    var bestMove
    if ((data.depth % 2) !== (depth % 2))
        bestMove = getBestMove(node, data.MIN, depth)
    else
        bestMove = getBestMove(node, data.MAX, depth)
    node.heuristic = bestMove.heuristic
    if (depth === data.depth)
        return {x : bestMove.x, y : bestMove.y}
    return false
}

const minmax = (node, depth) => {
    data.callsToMinMax += 1
    var nbNode = 0
    var pos = {x : 0, y : 0}
    var color = (data.depth % 2) === (depth % 2) ? data.iaColor : data.playerColor
    while (true){
        if (!data.board[pos.y][pos.x] && noStones(node, pos.x, pos.y)){
            node[nbNode] = getChild(pos, node, depth, color)
            if (depth > 1)
                minmax(node[nbNode], depth - 1)
            else
                node[nbNode].heuristic = getHeuristic(node[nbNode])
            if (getAlphaBetaPruning(node, depth, nbNode))
                break
            nbNode++
        }
        if (!increment(pos))
            break
    }
    var bestMove
    if ((bestMove = uploadHeuristic(depth, node)))
        return {x : bestMove.x, y : bestMove.y}
}

