const blockCase = (x, y, board, offset) => {
    var pos
    for (var c = 0; c < offset; c++){
        pos = [
            {x : x - (c + 1), y : y - (c + 1)},
            {x : x + (c + 1), y : y - (c + 1)},
            {x : x + (c + 1), y : y + (c + 1)},
            {x : x - (c + 1), y : y + (c + 1)}
        ]
        for (var i = 0; i < 3 + c * 2 - 1; i++){
            if (
                (
                    pos[0].x >= 0 && pos[0].x < 19 && pos[0].y >= 0 && pos[0].y < 19 &&
                    (board[pos[0].y][pos[0].x] === data.WHITE || board[pos[0].y][pos[0].x] === data.BLACK)
                ) ||
                (
                    pos[1].x >= 0 && pos[1].x < 19 && pos[1].y >= 0 && pos[1].y < 19 &&
                    (board[pos[1].y][pos[1].x] === data.WHITE || board[pos[1].y][pos[1].x] === data.BLACK)
                ) ||
                (
                    pos[2].x >= 0 && pos[2].x < 19 && pos[2].y >= 0 && pos[2].y < 19 &&
                    (board[pos[2].y][pos[2].x] === data.WHITE || board[pos[2].y][pos[2].x] === data.BLACK)
                ) ||
                (
                    pos[3].x >= 0 && pos[3].x < 19 && pos[3].y >= 0 && pos[3].y < 19 &&
                    (board[pos[3].y][pos[3].x] === data.WHITE || board[pos[3].y][pos[3].x] === data.BLACK)
                )
            )
                    return false
            pos[0].x++
            pos[1].y++
            pos[2].x--
            pos[3].y--

        }
    }
    return true
}

const blockBoard = (board, offset) => {
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if (!board[y][x] && blockCase(x, y, board, offset))
                board[y][x] = 3
        }
    }
}

