const printBackground = (data, images) => {
    data.ctx.fillStyle = 'white'
    data.ctx.fillRect(0, 0, data.canvas.width, data.canvas.height)
    for (var y = 0; y < 18; y++){
        for (var x = 0; x < 18; x++)
            data.ctx.drawImage(
                images.gomokuBoard,
                x * images.gomokuBoard.width + images.blackPiece.width / 2,
                y * images.gomokuBoard.height + images.blackPiece.width / 2
            )
    }
}

const initGame = (images) => {
    var data = {}
    data.canvas = document.getElementById('canvas')
    data.ctx = data.canvas.getContext('2d')
    printBackground(data, images)
    data.canvas.onmousemove = (e) => {
        canvasOffsetWidth = data.canvas.offsetWidth

        printBackground(data, images)
        data.ctx.drawImage(
            images.whitePiece,
            (Math.floor(e.offsetX / 19) * 19 * data.canvas.width) / data.canvas.offsetWidth,
            (Math.floor(e.offsetY / 19) * 19 * data.canvas.height) / data.canvas.offsetHeight
        )
    }
}

export default initGame
