import React, { useEffect, useState } from 'react'
import styles from './home.module.css'
import getImages from './getImages'

const Buttons = ({images}) => {

    const navigate = use

    return (
        <div className={styles.container}>
            <div className={styles.button} style={{backgroundImage:`url(${images[0].src}`}}>
                <div className={styles.hvsia} onClick={() => navigate('/humanvsia')}>
                </div>
            </div>
            <div className={styles.button} style={{backgroundImage:`url(${images[1].src}`}}>
                <div className={styles.hvsh}>
                </div>
            </div>
        </div>
    )
}

const Header = () => {
    return (
        <div className={styles.header}>
            <h1 className={styles.title}>GOMOKU</h1>
        </div>
    )
}

const Footer = () => {
    return (
        <div className={styles.footer}>
            <p>Made by Antoine Dauchy and Abdessamad Rbib</p>
        </div>
    )
}

const Home = () => {
    const [images, setImages] = useState(false)

    useEffect(() => {
        if (!images)
            getImages(setImages)
    }, [images])

    if (images){
        return (
            <div>
                <Header/>
                <Buttons images={images}/>
                <Footer/>
            </div>
        )
    }
    return (
        <h1 className={styles.loading}>
            LOADING...
        </h1>
    )
}

export default Home
