    var node = 0
    data.tree[treeLine + 1] = []
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if (!board[y][x] && !stones[`${x},${y}`]){
                if (depth > 1)
                    data.tree[treeLine + 1][node] = minmax(depth - 1)
                else
                    return heuristic(actualBoard(board, stones))
                node += 1
            }
        }
    }
}

const copyBoard = (board) => {
    const array = []
    for (var c = 0; c < 19; c++){
        array[c] = [...board[c]]
    }
    return array
}

