const checkTopAlignment = (x, y, board) => {
    var nb = 0
    var color = board[y][x]
    while (y >= 0 && nb < 5 && board[y][x] === color){
        y--
        nb++
    }
    if (nb === 5)
        return true
    return false
}

const checkAlignment = (x, y, board) => {
    if (checkTopAlignment(x, y, board))
        return true
    return false
}

const checkWinner = (data) => {
    for (var y = 0; y < 19; y++){
        for (var x = 0; x < 19; x++){
            if ((winner = checkAlignment(x, y, data.board)))
                return winner
        }
    }
    return false
}

const checkEndGame = (data) => {
    var winner = checkWinner(data)
    if (winner)
        return winner
    if (data.piecesOnBoard === 19*19)
        return 'draw'
    return false
}

export default checkEndGame
